ALTER TRIGGER dbo.validar_ins_convite_licitacaoON convite_licitacaoWITH ENCRYPTION FOR INSERTAS BEGIN	DECLARE @cod_proc_adm INT,			  @data_inicio DATETIME	SELECT		@cod_proc_adm 	= cod_proc_adm	,		@data_inicio	= data_abertura	FROM INSERTED 					INSERT INTO convite_estrutura_dfp(cod_proc_adm	, 												 cod_estru_dfp	, 												 cod_sit_conv_estru,												 data_inicio)									SELECT										@cod_proc_adm,										cod_estru_dfp,										1 				 ,										@data_inicio									FROM estrutura_dfp									WHERE padrao = 1		IF(@@ERROR <> 0)	BEGIN		ROLLBACK		RAISERROR('TG: Não foi possível gerar a estrutura padrão da D.F.P.',16,1)		RETURN	END	END